import React, {useEffect, useState} from 'react';
import {Carousel} from "react-bootstrap";

const Slider = () => {
    const [movies,setMovies] = useState([]);
    const getMovies = async ()=>{
        const api = await fetch(`https://api.themoviedb.org/3/trending/all/day?api_key=${process.env.API_KEY}`);
        const data = await api.json();
        setMovies(data.results)
    }
    useEffect(()=>{
        getMovies()
        },[])
    return (
        <Carousel>
            {movies.map((movie)=>{
                return(
                    <Carousel.Item>
                        <img src={movie.poster_path}/>
                        <Carousel.Caption>
                            <h2>{movie.title}</h2>
                        </Carousel.Caption>
                    </Carousel.Item>
                );


            })}
        </Carousel>
    );
};

export default Slider;
